import { Fragment, useEffect } from "react";
import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import Notification from "./components/UI/Notification";
import { useSelector, useDispatch } from "react-redux";
import { uiActions } from "./store/ui-slice";

function App() {
  const dispatch = useDispatch;
  const displayCart = useSelector((state) => state.ui.isVisible);
  const cart = useSelector((state) => state.items);
  const notification = useSelector((state) => state.ui.notification);

  console.log(notification);
  //allow state to update before sending fetch req,  waits for cart to change to reexecute fetch and replace old cart with new with PUT
  useEffect(() => {
    try {
      const sendCartData = async () => {
        dispatch(
          uiActions.showNotification({
            status: "pending",
            title: "Sending",
            message: "Sending cart data.",
          })
        );
        const response = await fetch(
          "https://reactmeals-5adac-default-rtdb.firebaseio.com/cart.son",
          {
            method: "PUT",
            body: JSON.stringify(cart),
          }
        );

        if (!response.ok) {
          throw new Error("Sending cart data failed");
        }
      };
    } catch {
      dispatch(
        uiActions.showNotification({
          status: "error",
          title: "Error!",
          message: "Failed to send cart data",
        })
      );
    }
  }, [cart, dispatch]);

  return (
    <Fragment>
      {notification && (
        <Notification
          status={notification.status}
          title={notification.title}
          message={notification.message}
        />
      )}
      <Layout>
        {displayCart && <Cart />}
        <Products />
      </Layout>
    </Fragment>
  );
}

export default App;
