import { createSlice } from "@reduxjs/toolkit";

const initialCart = { items: [], totalQuantity: 0 };

const itemsSlice = createSlice({
  name: "items",
  initialState: initialCart,
  reducers: {
    addItem(state, action) {
      const newItem = action.payload;
      const existingItem = state.item.find((item) => item.id === newItem.id); //check if already in items array

      if (!existingItem) {
        state.items.push({
          itemID: newItem.id,
          title: newItem.title,
          price: newItem.price,
          quantity: 1,
          totalPrice: newItem.price,
        });
      } else {
        existingItem.quantity++;
        existingItem.totalPrice = existingItem.totalPrice + newItem.price;
      }
    },
    removeItem(state, action) {
      const id = action.payload;
      const existingItem = state.items.find((item) => item.id === id);
      if (existingItem.quantity === 1) {
        state.items = state.items.filter((items) => items.id !== id); //filter by all item.id !== id and set a new state.items object
      } else {
        existingItem.quantity--;
        existingItem.totalPrice = existingItem.totalPrice - existingItem.price;
      }
    },
  },
});

export const itemsActions = itemsSlice.actions;

export default itemsSlice;
